import { createApp } from 'vue';
import App from './App.vue';
import router from './router'; // ここでルーターをインポート
import './assets/styles/main.css';  // グローバルスタイルをインポート

createApp(App)
  .use(router)  // アプリケーションにルーターを使わせる
  .mount('#app');
