export const works = [
  {
    id: 'ae1',
    title: 'デジタルアート1',
    category: 'art',
    year: '2024',
    image: require('@/assets/img/art/ae1.webp'),
    link: '/works/ae1',
    description: 'デジタルアート作品の説明文'
  },
  {
    id: 'butterfly',
    title: '蝶',
    category: 'drawing',
    year: '2024',
    image: require('@/assets/img/drawing/蝶.png'),
    description: '蝶のイラストの説明文'
  }
  // 他の作品をここに追加
];

// 詳細ページを持つ作品のルートを自動生成する関数
export const generateDetailRoutes = () => {
  return works
    .filter(work => work.link)
    .map(work => ({
      path: work.link,
      component: () => import('@/components/DetailPage.vue'),
      props: { work }
    }));
};
