/* global Masonry, imagesLoaded */

export function initMasonry(gridElement) {
  if (!gridElement) return;

  const masonry = new Masonry(gridElement, {
    percentPosition: false,
    columnWidth: 300,
    itemSelector: '.grid-item',
    gutter: 30,
    fitWidth: true
  });

  // 画像が読み込まれた後にレイアウトを更新
  imagesLoaded(gridElement).progress(() => {
    masonry.layout();
  });

  return masonry;
}

// Masonryミックスイン
export const masonryMixin = {
  mounted() {
    this.$nextTick(() => {
      const grid = this.$el.querySelector('.grid');
      if (!grid) return;

      // 画像の読み込みを待ってからMasonryを初期化
      imagesLoaded(grid, () => {
        this.masonry = new Masonry(grid, {
          itemSelector: '.grid-item',
          columnWidth: 300,
          gutter: 30,
          percentPosition: false,
          fitWidth: true
        });
      });
    });
  },

  // コンポーネントが更新されたときにレイアウトを再計算
  updated() {
    this.$nextTick(() => {
      if (this.masonry) {
        this.masonry.layout();
      }
    });
  },

  beforeDestroy() {
    if (this.masonry) {
      this.masonry.destroy();
    }
  }
};
