import { createRouter, createWebHistory } from 'vue-router';
import Home from '@/views/Home.vue';
import Works from '@/views/Works.vue';
import History from '@/views/History.vue';
import Request from '@/views/Request.vue';    
import Portfolio from '@/views/Portfolio.vue';
import DetailPage from '@/components/DetailPage.vue';
import { works } from '@/data/works';

// 詳細ページのルートを生成（contentがある作品のみ）
const generateDetailRoutes = () => {
  console.log('Generating detail routes for works:', works);
  const detailRoutes = works
    .filter(work => work.content)
    .map(work => ({
      path: `/works/${work.id}`,
      name: `work-${work.id}`,
      component: DetailPage,
      props: route => ({ id: route.params.id })
    }));
  console.log('Generated detail routes:', detailRoutes);
  return detailRoutes;
};

const routes = [
  {
    path: '/',
    name: 'Home',
    component: Home
  },
  {
    path: '/works',
    name: 'Works',
    component: Works
  },
  {
    path: '/history',
    name: 'History',
    component: History
  },  
  {
    path: '/request',
    name: 'Request',
    component: Request
  },
  {
    path: '/portfolio',
    name: 'Portfolio',
    component: Portfolio
  },
  ...generateDetailRoutes()
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
});

// ルートの変更をログに記録
router.beforeEach((to, from, next) => {
  console.log('Route navigation:', {
    to: to.path,
    params: to.params,
    props: to.props
  });
  next();
});

export default router;
