// Helper function to create a work entry
const createWork = (id, title, imagePath, options = {}) => ({
  id,
  title,
  image: require(`@/assets/img/${imagePath}`),
  year: options.year || '2024',
  link: options.link || `/works/${id}`,
  category: options.category,
  // 新しいプロパティ：カードとディテールページのコンテンツ
  cardContent: options.cardContent || '',  // カードのmetaスロットに表示される内容
  detailContent: options.detailContent || '', // DetailPageのスロットに表示される内容
  tags: options.tags || [],  // タグ一覧
});

// Helper function to create works for a category
const createCategoryWorks = (category, works) => {
  return works.map(work => ({
    ...work,
    category: category
  }));
};

// Art works
const artWorks = createCategoryWorks('art', [
  createWork('digital-art-1', 'デジタルアート作品1', 'art/digital-art-1.webp', {
    cardContent: `
      <p class="card-description">デジタルで描いた抽象画</p>
      <div class="card-tags">
        <span class="tag">Digital</span>
        <span class="tag">Abstract</span>
      </div>
    `,
    detailContent: `
      <article class="work-detail">
        <section class="concept">
          <h2>コンセプト</h2>
          <p>この作品は、デジタルの特性を活かした抽象的な表現を追求しています。
          色彩の重なりと形状の歪みを通じて、現代社会における人間の感情を表現しました。</p>
        </section>

        <section class="process">
          <h2>制作プロセス</h2>
          <p>Procreateを使用し、独自のブラシを作成して制作しました。
          レイヤーの重ね方や透明度の調整により、深みのある表現を目指しました。</p>
        </section>

        <section class="specs">
          <h2>仕様</h2>
          <ul>
            <li>使用ソフト：Procreate</li>
            <li>制作期間：2週間</li>
            <li>サイズ：3000x4000px</li>
          </ul>
        </section>
      </article>
    `,
    tags: ['Digital', 'Abstract', 'Procreate']
  }),
  // 他のアート作品も同様に定義...
]);

// Drawing works
const drawingWorks = createCategoryWorks('drawing', [
  createWork('sketch-1', 'スケッチ作品1', 'drawing/sketch-1.webp', {
    cardContent: `
      <p class="card-description">鉛筆とペンによるスケッチ</p>
      <div class="card-tags">
        <span class="tag">Pencil</span>
        <span class="tag">Ink</span>
      </div>
    `,
    detailContent: `
      <article class="work-detail">
        <section class="concept">
          <h2>作品について</h2>
          <p>日常の一コマを切り取ったスケッチです。
          光と影の対比を意識して描きました。</p>
        </section>

        <section class="materials">
          <h2>使用画材</h2>
          <ul>
            <li>鉛筆：Staedtler Mars Lumograph（2H, HB, 2B）</li>
            <li>ペン：Sakura Pigma Micron</li>
            <li>紙：Strathmore 300 Series</li>
          </ul>
        </section>
      </article>
    `,
    tags: ['Sketch', 'Traditional', 'Pencil']
  }),
  // 他のドローイング作品も同様に定義...
]);

// 他のカテゴリーの作品も同様に定義...

// すべての作品を結合
const works = [
  ...artWorks,
  ...drawingWorks,
  // 他のカテゴリーの作品も追加...
];

export { works };

// 詳細ページを持つ作品のルートを自動生成する関数
export const generateDetailRoutes = () => {
  return works
    .filter(work => work.link)
    .map(work => ({
      path: work.link,
      component: () => import('@/components/DetailPage.vue'),
      props: { work }
    }));
};
